mapper.update:
UPDATE wms_ware_sku
SET stock = stock + #{skuNum}
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId};

UPDATE wms_ware_sku
SET stock_locked = (stock_locked + #{num})
WHERE sku_id = #{skuId}
  AND ware_id = #{wareId}
  AND stock - stock_locked >= #{num}

UPDATE wms_ware_sku
SET stock_locked = stock_locked - #{lockedDetailTo.skuNum}
WHERE sku_id = #{lockedDetailTo.skuId}
  AND ware_id = #{lockedDetailTo.wareId}
  AND lock_status = 1
